mapper.update:
update product set name = #{name}, price = #{price}, size = #{size}, stock = #{stock}, info = #{info}, updated_by = #{updatedBy}, updated_date = now() where product_id = #{productId};
update category set category_big = #{bigCategory}, category_small = #{smallCategory}, updated_by = #{updatedBy}, updated_date = now() where category_id = #{categoryId};
update product_images set images_url = #{imagesUrl}, updated_by = #{updatedBy}, updated_date = now() where product_id = #{productId};
